import React from "react";
import "./CheckList.css";
import { useState } from "react";

export const CheckList = () => {
  const [db, setDb] = useState([
    { content: "content1", id: "0", checked: false },
    { content: "content2", id: "1", checked: false },
    { content: "content3", id: "2", checked: false },
  ]);

  const [todos, setTodos] = useState("");

  const addTodos = (value) => {
    let length = db.length.toString()
    setDb([...db, {content: value, id: length, checked:false}])
    setTodos("") //todos 값 초기화
  }

  const handleCheck = (index) => {
    let tmp = [...db];
    tmp[index].checked = !tmp[index].checked;
    setDb(tmp)
  }

  return (
    <div className="TDLContainer">
      <div className="TDLTitle">To dos</div>
      {db.map((value) => (
        <div key={value.id} className="TDLContentBox">
          <input type="checkBox" checked={value.checked} onChange={()=>handleCheck(parseInt(value.id))}></input>
          <div>{value.content}</div>
        </div>
      ))}
      <div className="TDLInputBox">
        <input
          className="TDLAddInput"
          placeholder="입력하세요..."
          onChange={(e) => setTodos(e.target.value)}
          value={todos}
        />
        <button className="TDLAddButton" onClick={() => addTodos(todos)}>
          Add
        </button>
      </div>
    </div>
  );
};
