import React from "react";
import "./CheckList.css";
import { useState, useEffect } from "react";
import { getDB, setDB } from "../services/doc.services";

export const CheckList = () => {
  const [db, setDb] = useState([]);
  const [todos, setTodos] = useState("");

  const addTodos = (value) => {
    let length = db.length.toString();
    setDb([...db, { content: value, id: length, checked: false }]);
    setTodos(""); //todos 값 초기화
  };

  const handleCheck = (id) => {
    let index = db.findIndex((value) => value.id === id);
    let tmp = [...db];
    tmp[index].checked = !tmp[index].checked;
    setDb(tmp);
  };

  useEffect(() => {
    if (getDB("toDosDB") === null) {
      setDB("toDosDB", []);
    } else {
      setTodos(getDB("toDosDB"));
    }
  }, []);

  return (
    <div className="TDLContainer">
      <div className="TDLTitle">To dos</div>
      {db.map((value) => (
        <div key={value.id} className="TDLContentBox">
          <input
            type="checkBox"
            checked={value.checked}
            onChange={() => handleCheck(value.id)}
          ></input>
          <div className={value.checked ? "checked" : ""}>{value.content}</div>
        </div>
      ))}
      <div className="TDLInputBox">
        <input
          className="TDLAddInput"
          placeholder="입력하세요..."
          onChange={(e) => setTodos(e.target.value)}
          value={todos}
        />
        <button className="TDLAddButton" onClick={() => addTodos(todos)}>
          Add
        </button>
      </div>
    </div>
  );
};
