import React from 'react'
import "./Modal.css"
import { useState } from 'react';

export const Modal = (props) => {
  const [modifytext, setModifytext] = useState(props.content);
  return (
    <div className="modalContainer">
      <div className="modalBox">
        <input className='TDLModifyingText' onChange={(e)=>setModifytext(e.target.value)} value={props.content}/>
        <button onClick={()=>{props.handleClose(); props.TDLmodify(modifytext)} }>Modify</button>
      </div>
    </div>
  )
}
